var React = require('react/addons'),
    _ = require('lodash');


var GridItem = React.createClass({
    getDefaultProps: function() {
        return {

        }
    },
    getGridProps: function() {
        return _.extend({}, this.props);
    },
    render: function() {
        return (
            <div {...this.props} __grid={this.getGridProps()}>
                {this.props.children}
            </div>
        )
    }

});

module.exports = GridItem;