var React = require('react'),
    _ = require('lodash'),
    ReactSelect = require('roadmapper/components/select/Select');

var ProjectSelector = React.createClass({
    getOptions: function() {
        return _.map(this.props.options, function(option) {
            return {value: option.id + '', label: option.fields.name}
        })
    },
    render: function() {
        return (
            <div>
                <ReactSelect {...this.props} options={this.getOptions()} />
            </div>  
        )
    }
});

module.exports = ProjectSelector;