var Fluxxor = require('Fluxxor');

var RoadmapStore = require('./stores/roadmap/RoadmapStore'),
    RoadmapActions = require('./stores/roadmap/RoadmapActions');

var ProjectStore = require('./stores/project/ProjectStore'),
    ProjectActions = require('./stores/project/ProjectActions');

    var stores = {
        RoadmapStore: new RoadmapStore(),
        ProjectStore: new ProjectStore()
    }
    var actions = {
        RoadmapActions: RoadmapActions,
        ProjectActions: ProjectActions
    }

module.exports = new Fluxxor.Flux(stores, actions);
