var ProjectConstants = require('./ProjectConstants'),
    JamaItemProxy = require('roadmapper/data/JamaItemProxy'),
    _ = require('lodash');

var ProjectActions = {
    loadProjects: function(payload) {
        JamaItemProxy.getProjects(_.bind(function(err, res) {
            this.dispatch(ProjectConstants.PROJECT_LOAD_PROJECTS_SUCCESS, res);
        }, this));
    }
}

module.exports = ProjectActions;