var Fluxxor = require('fluxxor'),
    ProjectConstants = require('./ProjectConstants'),
    Proxy = require('roadmapper/data/Proxy');

var ProjectStore = Fluxxor.createStore({
    initialize: function() {
        this.bindActions(
            ProjectConstants.PROJECT_LOAD_PROJECTS_SUCCESS, this.setProjects
        )
    },
    setProjects: function(projects) {
        this.projects = projects;
        this.emit('change');
    },
    getProjects: function() {
        return this.projects || [];
    }
});

module.exports = ProjectStore;